"use strict";(self.webpackChunkdocs_loreum_org=self.webpackChunkdocs_loreum_org||[]).push([[1567],{5226:e=>{e.exports=JSON.parse('{"version":{"pluginId":"default","version":"current","label":"Next","banner":null,"badge":false,"noIndex":false,"className":"docs-version-current","isLast":true,"docsSidebars":{"docs":[{"type":"link","label":"Introduction","href":"/","docId":"intro","unlisted":false},{"type":"link","label":"Mission","href":"/mission","docId":"mission","unlisted":false},{"type":"link","label":"Getting Started","href":"/getting-started","docId":"getting-started","unlisted":false},{"type":"link","label":"Network","href":"/network","docId":"network","unlisted":false},{"type":"link","label":"Architecture","href":"/architecture","docId":"architecture","unlisted":false},{"type":"link","label":"Governance","href":"/governance","docId":"governance","unlisted":false},{"type":"link","label":"Glossary","href":"/glossary","docId":"glossary","unlisted":false}]},"docs":{"architecture":{"id":"architecture","title":"Systems Architecture","description":"Overview","sidebar":"docs"},"getting-started":{"id":"getting-started","title":"Getting Started","description":"Welcome to the Loreum Network! This guide will walk you through the steps to set up your first Loreum Node, so you can start contributing to and benefiting from the network.","sidebar":"docs"},"glossary":{"id":"glossary","title":"Glossary","description":"1. Loreum Network: A decentralized ecosystem where human and AI agents collaborate seamlessly for innovation and governance.","sidebar":"docs"},"governance":{"id":"governance","title":"Governance","description":"Overview","sidebar":"docs"},"intro":{"id":"intro","title":"intro","description":"The Loreum Network is a decentralized super-intelligence ecosystem designed to enable seamless collaboration between human and AI agents. With a focus on fostering innovation, inclusivity, and sustainability, the network leverages distributed AI services and collaborative decision-making to create a secure and interoperable platform. Its vision is to democratize access to advanced technologies and data, empowering individuals and communities to innovate and govern responsibly.","sidebar":"docs"},"mission":{"id":"mission","title":"Mission","description":"The Loreum Network is a decentralized intelligence ecosystem that seamlessly integrates human and AI collaboration. Its vision is to foster innovation, inclusivity, and sustainability. It acts as a secure network of independent nodes, enabling collaboration, monetization, and governance while maintaining autonomy and privacy. The network democratizes access to advanced technologies and supports a wide range of applications, from decentralized AI services to data monetization and collaborative decision-making. It\u2019s a versatile platform for various industries.","sidebar":"docs"},"network":{"id":"network","title":"Network","description":"The Loreum Network blockchain essentially acts as a global index, enabling a decentralized and distributed query system. When you start a Loreum Node on your device, it syncs with this network and provides you with access to a vast, queryable index. Prompts sent through this network are intelligently routed to the most suitable nodes based on their sensor or agent capabilities and reputation. This setup ensures that the network is robust and adaptive.","sidebar":"docs"}}}}')}}]);